      "action": "created",
      "issue": {
        "url": "https://api.github.com/repos/rails/rails/issues/36844",
        "repository_url": "https://api.github.com/repos/rails/rails",
        "labels_url": "https://api.github.com/repos/rails/rails/issues/36844/labels{/name}",
        "comments_url": "https://api.github.com/repos/rails/rails/issues/36844/comments",
